import React, { useEffect, useState } from 'react';
import { getGameState, postMove, undoMove } from './api';
import './styles.css';

function App() {
  const [playerPos, setPlayerPos] = useState({ x: 0, y: 0 });
  const [board, setBoard] = useState([
    [' ', ' ', ' ', 'üéØ'],
    ['üü´', 'üü´', ' ', ' '],
    [' ', ' ', ' ', ' '],
    [' ', ' ', ' ', ' '],
  ]);

  useEffect(() => {
    async function fetchState() {
      const data = await getGameState();
      setPlayerPos(data);
    }
    fetchState();
  }, []);

  const handleMove = async (direction) => {
    const data = await postMove(direction);
    setPlayerPos(data);
  };

  const handleUndo = async () => {
    const data = await undoMove();
    setPlayerPos(data);
  };

  const renderCell = (x, y) => {
    if (playerPos.x === x && playerPos.y === y) return 'üßç';
    return board[y]?.[x] || ' ';
  };

  useEffect(() => {
    if (board[playerPos.y]?.[playerPos.x] === 'üéØ') {
      alert('üéâ You reached the target!');
    }
  }, [playerPos]);

  return (
    <div className="container">
      <div className="board">
        {board.map((row, y) => (
          <div key={y} className="row">
            {row.map((_, x) => (
              <span key={x} className="cell">{renderCell(x, y)}</span>
            ))}
          </div>
        ))}
      </div>

      <div className="controls">
        <button onClick={() => handleMove('UP')}>‚¨ÜÔ∏è</button>
        <div>
          <button onClick={() => handleMove('LEFT')}>‚¨ÖÔ∏è</button>
          <button onClick={() => handleMove('DOWN')}>‚¨áÔ∏è</button>
          <button onClick={() => handleMove('RIGHT')}>‚û°Ô∏è</button>
        </div>
        <button onClick={handleUndo}>‚è™ Undo</button>
      </div>
    </div>
  );
}

export default App;
