import React, { useState, useEffect } from "react";
import { postMove, getGameState } from "./api";

function App() {
  const [position, setPosition] = useState({ x: 0, y: 0 });

  const fetchState = async () => {
    const data = await getGameState();
    setPosition({ x: data.x, y: data.y });
  };

  const handleMove = async (direction) => {
    const newState = await movePlayer(direction);
    setPosition({ x: newState.x, y: newState.y });
  };

  useEffect(() => {
    fetchState();
  }, []);

  return (
    <div className="game-container">
      <h1>üåÄ Time Twister</h1>
      <p>Player Position: (X: {position.x}, Y: {position.y})</p>
      <div className="controls">
        <button onClick={() => handleMove("UP")}>‚Üë</button>
        <div>
          <button onClick={() => handleMove("LEFT")}>‚Üê</button>
          <button onClick={() => handleMove("RIGHT")}>‚Üí</button>
        </div>
        <button onClick={() => handleMove("DOWN")}>‚Üì</button>
      </div>
    </div>
  );
}

export default App;
