// time-twister-frontend/src/App.js
import React, { useState, useEffect } from "react";
import { getGameState, postMove } from "./api";
import "./styles.css";
import { undoMove } from "./api"; 

const maze = [
  [" ", " ", " ", " ", " "],
  [" ", "‚ñà", " ", "‚ñà", " "],
  [" ", " ", "‚ñà", " ", " "],
  [" ", "‚ñà", " ", " ", " "],
  [" ", " ", " ", " ", " "]
];

function App() {
  const [position, setPosition] = useState({ x: 0, y: 0 });
  const [target, setTarget] = useState({ x: 4, y: 4 });
  const [won, setWon] = useState(false);
  

  const fetchState = async () => {
    const data = await getGameState();
    const pos = data.x !== undefined ? data : data.position;
    setPosition(pos);
    setTarget(data.target || { x: 4, y: 4 }); // fallback if target not sent
    setWon(pos.x === (data.target?.x || 4) && pos.y === (data.target?.y || 4));
  };

const handleMove = (direction) => {
  const { x, y } = position;
  let newX = x;
  let newY = y;

  // Calculate new position
  if (direction === "UP") newY -= 1;
  else if (direction === "DOWN") newY += 1;
  else if (direction === "LEFT") newX -= 1;
  else if (direction === "RIGHT") newX += 1;

  // Boundary check
  if (
    newY < 0 || newY >= maze.length ||         // top/bottom boundary
    newX < 0 || newX >= maze[0].length         // left/right boundary
  ) {
    return; // invalid move: out of bounds
  }

  // Wall check
  if (maze[newY][newX] === "‚ñà") {
    return; // invalid move: hit a wall
  }

  // Valid move
  setPosition({ x: newX, y: newY });
};

  const handleUndo = async () => {
  const state = await undoMove();
  setPosition(state.position);


};


  useEffect(() => {
    fetchState();
  }, []);
//   if (!maze || !position || !target) {
//   return <div>Loading game...</div>;
// }

  return (
    <div className="container">
      <h1>Time Twister Maze üåÄ</h1>
      {won && <h2 className="win">üéâ You reached the goal! üéâ</h2>}
      <div className="grid">
        {maze.map((row, y) => (
          <div key={y} className="row">
            {row.map((cell, x) => {
              const isPlayer = position && position.x === x && position.y === y;
const isTarget = target && target.x === x && target.y === y;

              return (
                <div key={x} className={`cell ${cell === "‚ñà" ? "wall" : ""}`}>
                  {isPlayer ? "üßç" : isTarget ? "üèÅ" : cell}
                </div>
              );
            })}
          </div>
        ))}
      </div>
      <div className="controls">
        <button onClick={() => handleMove("UP")}>‚Üë</button>
        <div>
          <button onClick={() => handleMove("LEFT")}>‚Üê</button>
          <button onClick={() => handleMove("RIGHT")}>‚Üí</button>
        </div>
        <button onClick={() => handleMove("DOWN")}>‚Üì</button>
        
      </div >

      <div className = "undo">
      <button onClick={handleUndo}>Undo</button>
      </div>

    </div>
  );
}

export default App;
